# Simple example of taxiing as scripted behavior# import Pilot; a=Pilot.Pilot(); a.start()import mathimport calculation as calculationimport timefrom PidController import PidControllerimport Ckpt as Ckptimport Utilities as Utilities_deg2met = 110977.			# meters in one degree latitudeclass Pilot (Ckpt.Ckpt):			# subclass of the class Ckpt in the file Ckpt    def __init__(self):        super().__init__('HW4a', True, False)        self.stayTime = 0        self.angleController = PidController(0.1, 0.001, 0.001)        self.angleController.setPoint(0)        self.lastPit = 0    def ai(self, flyData, command):        command.throttle = 1        command.elevator = -0.7        if flyData.pitch > 90 or flyData.pitch < -90:            angleValue = self.angleController.calculateAngelPid(0)        elif self.lastPit > flyData.pitch:            if flyData.roll > 0:                angleValue = self.angleController.calculateAngelPid(abs(flyData.roll) - 180)            else:                angleValue = self.angleController.calculateAngelPid(180 - abs(flyData.roll))        else:            angleValue = self.angleController.calculateAngelPid(flyData.roll)        self.lastPit = flyData.pitch        command.aileron = angleValue / 5        # if flyData.pitch > 70:        #     command.aileron = -angleValue / 5        print("rol",flyData.roll)        print("ali ",command.aileron)        print("pit ",flyData.pitch)